sqlMap.select:
SELECT b.* FROM ( SELECT rownum rn, a.* FROM (
SELECT unq, category, ctgtype, ctggender, price, csize, color, thumbnail, hits, NVL( ( select AVG(mark) from review where goodsunq = goodsinfo.unq ),0) mark, ( (price/100)*(select off from sale where unq=goodsinfo.sale) ) saleprice, (select off from sale where unq=sale) off, substr(name,0,20) name FROM goodsinfo
) a ) b WHERE rn >= 1 AND rn <= 8

SELECT b.* FROM ( SELECT rownum rn, a.* FROM (
SELECT unq, category, ctgtype, ctggender, price, csize, color, thumbnail, hits, NVL( ( select AVG(mark) from review where goodsunq = goodsinfo.unq ),0) mark, ( (price/100)*(select off from sale where unq=goodsinfo.sale) ) saleprice, (select off from sale where unq=sale) off, substr(name,0,20) name FROM goodsinfo ORDER BY hits DESC
) a ) b WHERE rn >= 1 AND rn <= 8

SELECT b.* FROM ( SELECT rownum rn, a.* FROM (
SELECT unq, category, ctgtype, ctggender, price, csize, color, thumbnail, hits, sale, NVL( ( select AVG(mark) from review where goodsunq = goodsinfo.unq ),0) mark, ( (price/100)*(select off from sale where unq=goodsinfo.sale) ) saleprice, (select off from sale where unq=sale) off, substr(name,0,20) name FROM goodsinfo ORDER BY hits DESC
) a ) b WHERE rn >= 1 AND rn <= 8
